background-image: url("/images/loading.svg");
background-repeat: no-repeat;
background-size: 21px;
background-position: -2px 10px;
color: transparent;
}
$.getJSON("https:\/\/releases.admin.aspose.com/api/updatecount?path=78423208744649d59e4a167c3ba1b386&single=1", function(data) { $.each(data, function(key, value) {
$("#dwn-update-"+ key).css({"background":"none"});
$("#dwn-update-"+ key).css({"color":"#333"});
$("#dwn-update-"+ key).text(value.download_count);
$("#added-update-"+ key).text(value.date_added);
});
});

