Release Noteshttps://docs.aspose.com/html/net/aspose-html-for-net-22-10-release-notes
DescriptionIt contains Aspose.HTML for .NET 22.10 release.
.downloadcount {
background-image: url("/images/loading.svg");
background-repeat: no-repeat;
background-size: 21px;
background-position: -2px 10px;
color: transparent;
}
$.getJSON("https:\/\/releases.admin.aspose.com/api/updatecount?path=bfd433b104a2e90afe07f46bc8c14696-4-8077&single=1", function(data) { $.each(data, function(key, value) {
$("#dwn-update-"+ key).css({"background":"none"});
$("#dwn-update-"+ key).css({"color":"#333"});
$("#dwn-update-"+ key).text(value.download_count);
$("#added-update-"+ key).text(value.date_added);
});
});

