$.getJSON("https:\/\/releases.admin.aspose.com/api/updatecount?path=ec2c949e0b19815c5c316026b4efe7d3-3-9274&single=1", function(data) { $.each(data, function(key, value) {
$("#dwn-update-"+ key).css({"background":"none"});
$("#dwn-update-"+ key).css({"color":"#333"});
$("#dwn-update-"+ key).text(value.download_count);
$("#added-update-"+ key).text(value.date_added);
});
});

